<?php// Exit if accessed directlyif (!defined('ABSPATH')) exit;#-----------------------------------------------------------------## Creating new Comment List Template#-----------------------------------------------------------------#function liveto110_comment($comment, $args, $depth){	$GLOBALS['comment'] = $comment;	extract($args, EXTR_SKIP);	if ('div' == $args['style']) {		$tag = 'div';		$add_below = 'comment';	} else {		$tag = 'li';		$add_below = 'div-comment';	}	?>	<?php global $smof_data; ?>	<article id="div-comment-<?php comment_ID() ?>" class="comment">		<div class="row">			<div class="col-xs-2">				<div class="avatar-wrap">					<?php if ($args['avatar_size'] != 0) echo get_avatar($comment, 120); ?>				</div><!-- /.avatar -->			</div>			<div class="col-xs-10">				<div class="comment-body">					<div class="comment-author vcard">						<div class="reply">							<?php comment_reply_link(array_merge($args, array('add_below' => $add_below, 'depth' => $depth, 'max_depth' => $args['max_depth']))) ?>						</div><!-- /.reply -->						<div class="comment-info">							<?php printf(__('<cite class="fn">%s</cite>', 'liveto110'), get_comment_author_link()) ?>							<div class="comment-meta commentmetadata">								<a href="<?php echo htmlspecialchars(get_comment_link($comment->comment_ID)) ?>">									<?php										/* translators: 1: date, 2: time */										printf(__('%1$s at %2$s', 'liveto110'), get_comment_date(), get_comment_time()) 									?>								</a>								<?php edit_comment_link(__('(Edit)', 'liveto110'), '  ', ''); ?>							</div><!-- /.comment-meta .commentmetadata -->						</div><!-- /.comment-info -->					</div><!-- /.comment-author .vcard -->					<?php if ($comment->comment_approved == '0') { ?>						<em class="comment-awaiting-moderation"><?php _e('Your comment is awaiting moderation.', 'liveto110') ?></em>						<br/>					<?php } ?>					<div class="comment-entry">						<?php comment_text() ?>					</div><!-- /.comment-entry -->				</div><!-- /.comment-body -->			</div>		</div><!-- /.row -->	</article><!-- /.comment --><?php}// Do not delete these linesif (!empty($_SERVER['SCRIPT_FILENAME']) && 'comments.php' == basename($_SERVER['SCRIPT_FILENAME'])){	die ('Please do not load this page directly. Thanks!');}if (post_password_required()) { ?>	<p class="nocomments"><?php _e('This post is password protected. Enter the password to view comments.','liveto110'); ?></p>	<?php  return;}?><!-- You can start editing here. --><section id="comments" class="clearfix">	<header class="section-header">		<h1 class="section-title">			<?php comments_number(__('No Comments','liveto110'), __('1 Comment','liveto110'), __('% Comments','liveto110')); ?>		</h1><!-- /.section-title -->	</header><!-- /.section-header -->	<?php if (have_comments()) : ?>			<div class="commentlist">				<?php wp_list_comments('type=comment&callback=liveto110_comment'); ?>			</div><!-- /.commentlist -->			<div class="comments-navigation">				<div class="alignleft"><?php previous_comments_link() ?></div>				<div class="alignright"><?php next_comments_link() ?></div>			</div><!-- /.comments-navigation -->	<?php else : // this is displayed if there are no comments so far ?>		<?php if (comments_open()) : ?>			<!-- If comments are open, but there are no comments. -->		<?php else : // comments are closed ?>			<!-- If comments are closed. -->			<p class="nocomments"><?php _e('Comments are closed.','liveto110'); ?></p>		<?php endif;	endif;	if (comments_open()) :		$args = array(			'id_form'           => 'commentform',			'id_submit'         => 'submit',			'title_reply'       => __( 'Leave a Reply', 'liveto110' ),			'title_reply_to'    => __( 'Leave a Reply to %s', 'liveto110' ),			'cancel_reply_link' => __( 'Cancel Reply', 'liveto110' ),			'label_submit'      => __( 'Post Comment', 'liveto110' ),			'comment_field' 	=>  				'<div class="row">					<div class="col-xs-8">						<textarea id="comment" name="comment" placeholder="Comment"></textarea>					</div>				</div>'			,			'must_log_in' =>				'<p class="must-log-in">' .					sprintf(						__( 'You must be <a href="%s">logged in</a> to post a comment.', 'liveto110' ),						wp_login_url( apply_filters( 'the_permalink', get_permalink() ) )					) .				'</p>'			,			'logged_in_as' =>				'<p class="logged-in-as">' .					sprintf(						__( 'Logged in as <a href="%1$s">%2$s</a>. <a href="%3$s" title="Log out of this account">Log out?</a>', 'liveto110' ),						admin_url( 'profile.php' ),						$user_identity,						wp_logout_url( apply_filters( 'the_permalink', get_permalink( ) ) )					) .				'</p>'			,			'comment_notes_before' =>				'<p class="comment-notes">' .				__( 'Your email address will not be published.', 'liveto110' ) .				'</p>'			,			'comment_notes_after' =>				'<p class="form-allowed-tags">' .					sprintf(						__( 'You may use these <abbr title="HyperText Markup Language">HTML</abbr> tags and attributes: %s', 'liveto110' ),						' <code>' . allowed_tags() . '</code>'					) .				'</p>'			,			'fields' => apply_filters( 'comment_form_default_fields', array(				'author' 	=> 					'<div class="row">						<div class="col-xs-4">							<input id="author" name="author" type="text" value="' . esc_attr( $commenter['comment_author'] ) .'" placeholder="Name" />'				,							'email' 	=> '<input id="email" class="span12 black" name="email" type="text" value="' . esc_attr(  $commenter['comment_author_email'] ) .'" placeholder="Email" />'				,							'url' 		=> '<input class="span12 black" id="url" name="url" type="text" value="' . esc_attr( $commenter['comment_author_url'] ) .'" placeholder="Website" />						</div>					</div>'			)		),	);	comment_form($args);endif;